// RegistrationForm.js
import React, { useState } from 'react';
import './App.css';

const App = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    website: '',
    imagelink: '',
    gender: 'male',
    skills: {
      js: false,
      CSS: false,
      python: false,
    },
  });

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData((prevData) => {
      if (type === 'checkbox') {
        return {
          ...prevData,
          skills: {
            ...prevData.skills,
            [name]: checked,
          },
        };
      } else {
        return {
          ...prevData,
          [name]: value,
        };
      }
    });
  };

  const handleEnroll = () => {
    // Store the form data in local storage
    localStorage.setItem('enrolledStudent', JSON.stringify(formData));
    // Display the form data on the console for demonstration purposes
    console.log('Enrolled Student:', formData);
    // You can add logic here to store the data, make an API call, etc.
  };

  const displayEnrolledStudent = () => {
    // Retrieve the data from local storage
    const storedData = localStorage.getItem('enrolledStudent');
    if (storedData) {
      const parsedData = JSON.parse(storedData);
      return (
        <div className='enroll-student'>
          <h2>Enrolled Student Information:</h2>
          <pre>{JSON.stringify(parsedData, null, 2)}</pre>
        </div>
      );
    }
    return null;
  };

  const handleClear = () => {
    // Clear the form data
    setFormData({
      name: '',
      email: '',
      website: '',
      imagelink: '',
      gender: 'male',
      skills: {
        js: false,
        CSS: false,
        python: false,
      },
    });
  };

  return (
    <div className="bg-container">
      <div className='reg-card'>
        <h2>Registration Form</h2>
        <form>
          {/* ... (previous form code) */}
          <div>
            <button type="button" onClick={handleEnroll}>
              Enroll Student
            </button>
            <button type="button" onClick={handleClear}>
              Clear
            </button>
          </div>
        </form>
      </div>
      {displayEnrolledStudent()}
    </div>
  );
};

export default App;
