import React from 'react';
import './index.css';

const AppointmentItem = ({ appointmentDetails, toggleIsStarred }) => {
  const { id, title, email, gender, website, image, skills, isStarred } = appointmentDetails;
  const starImgUrl = isStarred
    ? 'https://assets.ccbp.in/frontend/react-js/appointments-app/filled-star-img.png'
    : 'https://assets.ccbp.in/frontend/react-js/appointments-app/star-img.png';

  const onClickStar = () => {
    toggleIsStarred(id);
  };

  return (
    <tr>
    <li className="appointment-item">
      <div className="header-container">
        <p className="title">{title}</p>
        <button
          type="button"
          data-testid="star"
          className="star-button"
          onClick={onClickStar}
        >
          <img src={starImgUrl} className="star" alt="star" />
        </button>
      </div>
      <p className="date"> {email}</p>
      <p className="date"> {website}</p>
      <p className="date"> {gender}</p>
      <p className="skills"> {skills.join(', ')}</p>
      <div>
      <p className="date"> {image}</p>
      </div>
    </li>
    </tr>
  );
};

export default AppointmentItem;
