import React, { useState } from 'react';
import { v4 as uuidv4 } from 'uuid';
import AppointmentItem from '../AppointmentItem';
import './index.css';

const Appointments = () => {
  // State for managing appointments
  const [appointmentsList, setAppointmentsList] = useState([]);
  // States for form inputs
  const [genderInput, setGenderInput] = useState('');

  const [titleInput, setTitleInput] = useState('');
  const [emailInput, setEmailInput] = useState('');
  const [websiteInput, setWebsiteInput] = useState('');
  const [fileInput, setFileInput] = useState('');
  const [skillsInput, setSkillsInput] = useState({
    html: false,
    css: false,
    js: false,
    python: false,
  });
  // State for filter
  const [isFilterActive, ] = useState(false);

  // Function to toggle starred status of an appointment
  const toggleIsStarred = (id) => {
    setAppointmentsList((prevAppointmentsList) =>
      prevAppointmentsList.map((eachAppointment) =>
        id === eachAppointment.id
          ? { ...eachAppointment, isStarred: !eachAppointment.isStarred }
          : eachAppointment
      )
    );
  };



  const onChangeSkillsInput = (skill) => {
    setSkillsInput((prevSkillsInput) => ({
      ...prevSkillsInput,
      [skill]: !prevSkillsInput[skill],
    }));
  };
  const onChangeGenderInput = (event) => {
    setGenderInput(event.target.value);
  };
  // Function to handle change in email input
  const onChangeEmailInput = (event) => {
    setEmailInput(event.target.value);
  };

  // Function to handle change in title input
  const onChangeTitleInput = (event) => {
    setTitleInput(event.target.value);
  };

  // Function to handle change in website input
  const onChangeWebsiteInput = (event) => {
    setWebsiteInput(event.target.value);
  };

  // Function to handle change in file input
  const onChangeFileInput = (event) => {
    setFileInput(event.target.value);
  };

  // Function to handle form submission
  const onAddAppointment = (event) => {
    event.preventDefault();

    // Creating a new appointment object
    const newAppointment = {
      id: uuidv4(),
      title: titleInput,
      email: emailInput,
      gender:genderInput,
      website: websiteInput,
      image: fileInput,
      skills: Object.keys(skillsInput).filter((skill) => skillsInput[skill]), 
      isStarred: false,
    };

    // Updating the appointments list
    setAppointmentsList((prevAppointmentsList) => [
      ...prevAppointmentsList,
      newAppointment,
    ]);

    // Resetting form inputs
    setTitleInput('');
    setEmailInput('');
    setWebsiteInput('');
    setFileInput('');
    setGenderInput(''); 
    setSkillsInput({
      html: false,
      css: false,
      js: false,
      python: false,
    });
  };

  // Function to filter appointments based on starred status
  const getFilteredAppointmentsList = () => {
    if (isFilterActive) {
      return appointmentsList.filter(
        (eachAppointment) => eachAppointment.isStarred === true
      );
    }
    return appointmentsList;
  };

  // Filtering the list of appointments
  const filteredAppointmentsList = getFilteredAppointmentsList();

  // Dynamically setting the filter button class

  // Function to display entered data in UI
  // Rendering the component
  return (
    <div className="app-container">
            <div className="responsive-container">
        <div>
      <h1 className="add-appointment-heading">Add Appointment</h1>
      </div>
      

        <div className="appointments-container">

          <div className="add-appointment-container">
            
            <form className="form" onSubmit={onAddAppointment}>
                <label htmlFor="title" className="label">
                  TITLE
                </label>
                <input
                  type="text"
                  id="title"
                  value={titleInput}
                  onChange={onChangeTitleInput}
                  className="input"
                  placeholder="Title"
                />
                <label htmlFor="email" className="label">
                  Email
                </label>
                <input
                  type="email"
                  id="email"
                  value={emailInput}
                  onChange={onChangeEmailInput}
                  className="input"
                />
              <div className='gender'>
                <label htmlFor="gender" className="label">
                  Gender
                </label>
                <div className="radio-group">
                  <label>
                    <input
                      type="radio"
                      id="male"
                      name="gender"
                      value="Male"
                      checked={genderInput === 'Male'}
                      onChange={onChangeGenderInput}
                    />
                    Male
                  </label>
                  <label>
                    <input
                      type="radio"
                      id="female"
                      name="gender"
                      value="Female"
                      checked={genderInput === 'Female'}
                      onChange={onChangeGenderInput}
                    />
                    Female
                  </label>
                </div>
              </div>
                <label htmlFor="website" className="label">
                  Website
                </label>
                <input
                  type="text"
                  id="website"
                  value={websiteInput}
                  onChange={onChangeWebsiteInput}
                  className="input"
                />
              <label htmlFor="image" className="label">
                Image Link
              </label>
              <input
                type="file"
                id="image"
                value={fileInput}
                onChange={onChangeFileInput}
                className="input"
              />
            
            <div className='skills'>

            <label htmlFor="skills" className="label">Skills</label>
            <div className="checkbox-group">
                <label>
                  <input
                    type="checkbox"
                    id="html"
                    checked={skillsInput.html}
                    onChange={() => onChangeSkillsInput('html')}
                  />
                  HTML
                </label>
                <label>
                  <input
                    type="checkbox"
                    id="css"
                    checked={skillsInput.css}
                    onChange={() => onChangeSkillsInput('css')}
                  />
                  CSS
                </label>
                <label>
                  <input
                    type="checkbox"
                    id="js"
                    checked={skillsInput.js}
                    onChange={() => onChangeSkillsInput('js')}
                  />
                  JavaScript
                </label>
                <label>
                  <input
                    type="checkbox"
                    id="python"
                    checked={skillsInput.python}
                    onChange={() => onChangeSkillsInput('python')}
                  />
                  Python
                </label>
              </div>
              </div>
              <button type="submit" className="add-button">
                Add
              </button>
            </form>
          </div>
          {/* Vertical Line */}
          <div className="vertical-line"></div>

          {/* List of Appointments Section */}
          <ul className="appointments-list">
            {filteredAppointmentsList.map((eachAppointment) => (
              <AppointmentItem
                key={eachAppointment.id}
                appointmentDetails={eachAppointment}
                toggleIsStarred={toggleIsStarred}
              />
            ))}
          </ul>

        </div>
      </div>
    </div>
  );
};

export default Appointments;
